def _recursive_add_systems(neo4j_client, parent_id, systems_dict):
    """Recursively add systems and their subsystems to the Neo4j database."""
    for system_id, system_data in systems_dict.items():
        # Create the system
        neo4j_client.create_system(system_id, system_data["name"])

        # Connect to parent if parent_id is provided
        if parent_id:
            neo4j_client.create_subsystem_relationship(parent_id, system_id)

        # Process subsystems if any
        if "subsystems" in system_data:
            _recursive_add_systems(neo4j_client, system_id, system_data["subsystems"])


def initialize_databases():
    """Initialize the databases with predefined airplane systems."""
    try:
        # Initialize Neo4j with the system hierarchy
        neo4j_client = Neo4jClient()
        mongodb_client = MongoDBClient()

        logger.info("Initializing Neo4j database with airplane systems...")
        _recursive_add_systems(neo4j_client, None, AIRPLANE_SYSTEMS)

        # Add all systems to MongoDB (including airplane)
        logger.info("Adding systems to MongoDB...")

        # 添加飞机根系统
        mongodb_client.save_system(
            system_id="airplane",
            system_name=AIRPLANE_SYSTEMS["airplane"]["name"],
            requirement=SYSTEM_REQUIREMENTS.get("airplane", ""),
            sysml=SYSTEM_SYSML_MODELS.get("airplane", ""),
        )

        # 添加所有子系统
        for system_id, system_data in AIRPLANE_SYSTEMS["airplane"][
            "subsystems"
        ].items():
            # 获取系统名称
            system_name = system_data["name"]

            # 获取需求说明和SysML模型
            requirement = SYSTEM_REQUIREMENTS.get(system_id, "")
            sysml = SYSTEM_SYSML_MODELS.get(system_id, "")

            # 保存系统数据到MongoDB
            mongodb_client.save_system(
                system_id=system_id,
                system_name=system_name,
                requirement=requirement,
                sysml=sysml,
            )

        logger.info("Database initialization completed successfully!")

        neo4j_client.close()
        mongodb_client.close()

    except Exception as e:
        logger.error(f"Error initializing databases: {str(e)}")
        raise


def delete_all_nodes():
    """删除Neo4j中的所有节点和关系，以及MongoDB中的所有文档"""
    try:
        neo4j_client = Neo4jClient()
        mongodb_client = MongoDBClient()

        # 删除Neo4j中的所有节点和关系
        neo4j_client.delete_all_nodes()
        logger.info("Successfully deleted all nodes and relationships from Neo4j")

        # 删除MongoDB中的所有文档
        mongodb_client.db[mongodb_client.systems.name].drop()
        logger.info("Successfully dropped MongoDB collection")

        # 重新创建索引
        mongodb_client.systems.create_index("system_id", unique=True)

        neo4j_client.close()
        mongodb_client.close()

    except Exception as e:
        logger.error(f"Error deleting data: {str(e)}")
        raise


if __name__ == "__main__":
    # 首先删除所有数据
    delete_all_nodes()
    # 然后重新初始化
    initialize_databases()
